INSERT INTO `wp_posts` VALUES (22,1,'2025-04-17 21:10:48','2025-04-17 11:10:48','<!-- wp:paragraph -->\n<p></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Determine if your A/B test results are statistically significant and make data-driven decisions with confidence.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:heading {\"level\":3} -->\n<h3 class=\"wp-block-heading\">How to use the A/B test calculator</h3>\n<!-- /wp:heading -->\n\n<!-- wp:code -->\n<pre class=\"wp-block-code\"><code></code></pre>\n<!-- /wp:code -->\n\n<!-- wp:code -->\n<pre class=\"wp-block-code\"><code>&lt;iframe src=\"https://your-app.up.railway.app\" width=\"100%\" height=\"500\" style=\"border:none;\">&lt;/iframe></code></pre>\n<!-- /wp:code -->\n\n<!-- wp:html -->\nimport { useState } from \'react\';\n\nexport default function ABTestCalculator() {\n  const [formData, setFormData] = useState({\n    controlVisitors: \'\',\n    controlConversions: \'\',\n    variantVisitors: \'\',\n    variantConversions: \'\',\n  });\n  \n  const [results, setResults] = useState(null);\n  const [error, setError] = useState(\'\');\n\n  const handleInputChange = (e) => {\n    const { name, value } = e.target;\n    setFormData({\n      ...formData,\n      [name]: value,\n    });\n  };\n\n  const calculateSignificance = () => {\n    // Parse inputs and validate\n    const control = {\n      visitors: parseInt(formData.controlVisitors, 10),\n      conversions: parseInt(formData.controlConversions, 10)\n    };\n    \n    const variant = {\n      visitors: parseInt(formData.variantVisitors, 10),\n      conversions: parseInt(formData.variantConversions, 10)\n    };\n\n    // Validation\n    if (isNaN(control.visitors) || isNaN(control.conversions) || \n        isNaN(variant.visitors) || isNaN(variant.conversions)) {\n      setError(\'Please enter valid numbers for all fields\');\n      setResults(null);\n      return;\n    }\n    \n    if (control.visitors <= 0 || variant.visitors <= 0) {\n      setError(\'Visitor counts must be positive numbers\');\n      setResults(null);\n      return;\n    }\n    \n    if (control.conversions < 0 || variant.conversions < 0) {\n      setError(\'Conversion counts cannot be negative\');\n      setResults(null);\n      return;\n    }\n    \n    if (control.conversions > control.visitors || variant.conversions > variant.visitors) {\n      setError(\'Conversions cannot exceed visitors\');\n      setResults(null);\n      return;\n    }\n\n    // Calculate conversion rates\n    const controlRate = control.conversions / control.visitors;\n    const variantRate = variant.conversions / variant.visitors;\n    const improvement = ((variantRate - controlRate) / controlRate) * 100;\n\n    // Calculate pooled standard error\n    const pooledConversionRate = (control.conversions + variant.conversions) / (control.visitors + variant.visitors);\n    const standardError = Math.sqrt(\n      pooledConversionRate * (1 - pooledConversionRate) * (1/control.visitors + 1/variant.visitors)\n    );\n    \n    // Calculate z-score\n    const zScore = (variantRate - controlRate) / standardError;\n    \n    // Calculate p-value using normal approximation\n    // This is a simplified approach - for a more accurate calculation, \n    // you might want to use a dedicated statistics library\n    const pValue = calculatePValue(zScore);\n    \n    // Calculate confidence interval\n    const criticalValue = 1.96; // 95% confidence level\n    const marginOfError = criticalValue * standardError;\n    const ciLower = ((variantRate - controlRate) - marginOfError) * 100;\n    const ciUpper = ((variantRate - controlRate) + marginOfError) * 100;\n\n    setResults({\n      controlRate: controlRate * 100,\n      variantRate: variantRate * 100,\n      improvement,\n      zScore,\n      pValue,\n      significant: pValue < 0.05,\n      confidenceInterval: [ciLower, ciUpper]\n    });\n    \n    setError(\'\');\n  };\n\n  // Calculate p-value from z-score (simplified approximation)\n  const calculatePValue = (z) => {\n    // This is a simplified approximation of the normal CDF\n    // For a more accurate calculation, consider using a statistics library\n    z = Math.abs(z);\n    const b0 = 0.2316419;\n    const b1 = 0.319381530;\n    const b2 = -0.356563782;\n    const b3 = 1.781477937;\n    const b4 = -1.821255978;\n    const b5 = 1.330274429;\n    \n    if (z > 6) return 0; // For extreme values\n    \n    const t = 1 / (1 + b0 * z);\n    const PDF = Math.exp(-0.5 * z * z) / Math.sqrt(2 * Math.PI);\n    const CDF = 1 - PDF * (b1 * t + b2 * Math.pow(t, 2) + b3 * Math.pow(t, 3) + b4 * Math.pow(t, 4) + b5 * Math.pow(t, 5));\n    \n    return 2 * (1 - CDF); // Two-tailed p-value\n  };\n\n  return (\n    <div className=\"bg-white p-6 rounded-lg shadow-md max-w-2xl mx-auto\">\n      <h2 className=\"text-2xl font-bold mb-6 text-gray-800\">A/B Testing Statistical Significance Calculator</h2>\n      \n      <div className=\"grid grid-cols-1 md:grid-cols-2 gap-6 mb-6\">\n        <div className=\"bg-blue-50 p-4 rounded-md\">\n          <h3 className=\"text-lg font-semibold mb-3 text-blue-700\">Control (A)</h3>\n          <div className=\"mb-3\">\n            <label className=\"block text-sm font-medium text-gray-700 mb-1\">Visitors</label>\n            <input\n              type=\"number\"\n              name=\"controlVisitors\"\n              value={formData.controlVisitors}\n              onChange={handleInputChange}\n              className=\"w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500\"\n              placeholder=\"e.g., 5000\"\n            />\n          </div>\n          <div>\n            <label className=\"block text-sm font-medium text-gray-700 mb-1\">Conversions</label>\n            <input\n              type=\"number\"\n              name=\"controlConversions\"\n              value={formData.controlConversions}\n              onChange={handleInputChange}\n              className=\"w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500\"\n              placeholder=\"e.g., 500\"\n            />\n          </div>\n        </div>\n        \n        <div className=\"bg-green-50 p-4 rounded-md\">\n          <h3 className=\"text-lg font-semibold mb-3 text-green-700\">Variant (B)</h3>\n          <div className=\"mb-3\">\n            <label className=\"block text-sm font-medium text-gray-700 mb-1\">Visitors</label>\n            <input\n              type=\"number\"\n              name=\"variantVisitors\"\n              value={formData.variantVisitors}\n              onChange={handleInputChange}\n              className=\"w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500\"\n              placeholder=\"e.g., 5000\"\n            />\n          </div>\n          <div>\n            <label className=\"block text-sm font-medium text-gray-700 mb-1\">Conversions</label>\n            <input\n              type=\"number\"\n              name=\"variantConversions\"\n              value={formData.variantConversions}\n              onChange={handleInputChange}\n              className=\"w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500\"\n              placeholder=\"e.g., 550\"\n            />\n          </div>\n        </div>\n      </div>\n      \n      <div className=\"mb-6\">\n        <button\n          onClick={calculateSignificance}\n          className=\"w-full bg-blue-600 hover:bg-blue-700 text-white font-medium py-2 px-4 rounded-md transition duration-200\"\n        >\n          Calculate Significance\n        </button>\n      </div>\n      \n      {error && (\n        <div className=\"mb-6 p-3 bg-red-50 border border-red-200 rounded-md text-red-600\">\n          {error}\n        </div>\n      )}\n      \n      {results && (\n        <div className=\"bg-gray-50 p-4 rounded-md\">\n          <h3 className=\"text-lg font-semibold mb-4\">Results</h3>\n          \n          <div className=\"grid grid-cols-2 gap-4 mb-4\">\n            <div>\n              <p className=\"text-sm text-gray-600\">Control Conversion Rate</p>\n              <p className=\"font-medium\">{results.controlRate.toFixed(2)}%</p>\n            </div>\n            <div>\n              <p className=\"text-sm text-gray-600\">Variant Conversion Rate</p>\n              <p className=\"font-medium\">{results.variantRate.toFixed(2)}%</p>\n            </div>\n          </div>\n          \n          <div className=\"mb-4\">\n            <p className=\"text-sm text-gray-600\">Relative Improvement</p>\n            <p className={`font-medium ${results.improvement >= 0 ? \'text-green-600\' : \'text-red-600\'}`}>\n              {results.improvement >= 0 ? \'+\' : \'\'}{results.improvement.toFixed(2)}%\n            </p>\n          </div>\n          \n          <div className=\"mb-4\">\n            <p className=\"text-sm text-gray-600\">95% Confidence Interval</p>\n            <p className=\"font-medium\">\n              {results.confidenceInterval[0].toFixed(2)}% to {results.confidenceInterval[1].toFixed(2)}%\n            </p>\n          </div>\n          \n          <div className=\"mb-4\">\n            <p className=\"text-sm text-gray-600\">p-value</p>\n            <p className=\"font-medium\">{results.pValue.toFixed(4)}</p>\n          </div>\n          \n          <div className=\"p-3 rounded-md font-medium text-center\" \n            style={{ \n              backgroundColor: results.significant ? \'#dcfce7\' : \'#fee2e2\',\n              color: results.significant ? \'#166534\' : \'#991b1b\'\n            }}>\n            {results.significant \n              ? \'Statistically Significant (95% confidence)\' \n              : \'Not Statistically Significant\'}\n          </div>\n        </div>\n      )}\n    </div>\n  );\n}\n<!-- /wp:html -->\n\n<!-- wp:paragraph -->\n<p>This tool helps you determine if your A/B test results are statistically significant: </p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>1. Enter the number of visitors and conversions for both your control (A) and test (B) groups </p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>2. Select your desired confidence level (typically 95%) </p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>3. Calculate the statistical significance </p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>4. Review the p-value, confidence interval, and relative improvement </p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>5. Get a clear recommendation on whether to implement the tested variation </p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>6. Understand the minimum sample size needed for reliable results All statistical calculations run in your browser - no data transmission required.</p>\n<!-- /wp:paragraph -->','A/B Test Significance - free online tool','','inherit','closed','closed','','20-revision-v1','','','2025-04-17 21:10:48','2025-04-17 11:10:48','',20,'http://marketingdata.local/?p=22',0,'revision','',0);
